mapper.update:
update setmeal
name = #{name}, category_id = #{categoryId}, price = #{price}, image = #{image}, description = #{description}, status = #{status}, update_time = #{updateTime}, update_user = #{updateUser},
where id = #{id}

mapper.delete:
delete from setmeal
where id in

    #{id}

mapper.select:
select d.* , c.name as categoryName from setmeal d left outer join category c on d.category_id = c.id


        and name like concat('%',#{name},'%')


        and d.category_id = #{categoryId}


        and d.status = #{status}


order by d.id desc
